export default MiddlewareService;
/**
 * Orchestrates dynamic middleware registered through the Medusa Middleware API
 */
declare class MiddlewareService {
    constructor(container: any);
    postAuthentication_: any[];
    preAuthentication_: any[];
    preCartCreation_: any[];
    routers: {};
    addRouter(path: any, router: any): void;
    getRouters(path: any): any;
    /**
     * Validates a middleware function, throws if fn is not of type function.
     * @param {function} fn - the middleware function to validate.
     */
    validateMiddleware_(fn: Function): void;
    /**
     * Adds a middleware function to be called after authentication is completed.
     * @param {function} middleware - the middleware function. Should return a
     *   middleware function.
     * @param {object} options - the arguments that will be passed to the
     *   middleware
     * @return {void}
     */
    addPostAuthentication(middleware: Function, options: object): void;
    /**
     * Adds a middleware function to be called before authentication is completed.
     * @param {function} middleware - the middleware function. Should return a
     *   middleware function.
     * @param {object} options - the arguments that will be passed to the
     *   middleware
     * @return {void}
     */
    addPreAuthentication(middleware: Function, options: object): void;
    /**
     * Adds a middleware function to be called before cart creation
     * @param {function} middleware - the middleware function. Should return a
     *   middleware function.
     * @return {void}
     */
    addPreCartCreation(middleware: Function): void;
    /**
     * Adds post authentication middleware to an express app.
     * @param {ExpressApp} app - the express app to add the middleware to
     * @return {void}
     */
    usePostAuthentication(app: ExpressApp): void;
    /**
     * Adds pre authentication middleware to an express app.
     * @param {ExpressApp} app - the express app to add the middleware to
     * @return {void}
     */
    usePreAuthentication(app: ExpressApp): void;
    usePreCartCreation(): any[];
}
